// Assume $inputUrls is the input array of URLs\n$inputUrls = [\n    'http://i41.tinypic.com/3342r93.jpg',\n    'http://i41.tinypic.com/28cfub7.jpg',\n    'http://i41.tinypic.com/12dsa32.jpg'\n];\n\n// Assume $outputData is the output array to be populated\n$outputData = [];\n\n// Assume $databaseResults is an associative array or some structure that holds URL -> data mappings from your database\n$databaseResults = [\n    'http://i41.tinypic.com/28cfub7.jpg' => [\n        'id' => 44,\n        'name' => '28cfub7.jpg',\n        'size' => 105544,\n        'descr' => '',\n        'password' => '',\n        'delete_id' => 'D392',\n        'upload_id' => '6676FD881'\n    ],\n    'http://i41.tinypic.com/12dsa32.jpg' => [\n        'id' => 45,\n        'name' => '12dsa32.jpg',\n        'size' => 49000,\n        'descr' => '',\n        'password' => '',\n        'delete_id' => '54C9',\n        'upload_id' => 'A58614C01'\n    ]\n];\n\n// Process each URL\nforeach ($inputUrls as $index => $url) {\n    if (isset($databaseResults[$url])) {\n        // If the URL exists in the database, insert the data into the output array at the correct position\n        array_splice($outputData, $index, 0, [$databaseResults[$url]]);\n    }\n}\n\n// $outputData now contains the desired result\nprint_r($outputData);