class QueryBuilder {\n    // Methods like select(), where(), get() here\n}\n\nclass ActiveRecord extends QueryBuilder {\n    protected $table;\n    protected $fields;\n\n    public function save() { /* Saves the current AR instance to the database */ }\n}\n\n// Usage example:\n$user = new ActiveRecord();\n$user->table = "users";\n$user->name = 'Fred';\n$user->save();\n\n$results = $user->where('id > 4')->get($user->table);\n\nwhile ($userRow = $results->fetch()) {\n    $userObj = new ActiveRecord();\n    $userObj->loadData($userRow); // Assuming a method to load data from an array\n    $userObj->active = TRUE;\n    $userObj->save();\n}