// Assuming $row is the result set row after fetching\n$intValue = (int)$row['numeric_column']; // Cast to integer\n\n// Alternatively, if using PDO or similar, you can use prepare + fetch with bindColumn for type conversion\n$stmt = $this->db->prepare('SELECT numeric_column FROM your_table WHERE id = ?');\n$stmt->bindValue(1, $id);\n$stmt->execute();\n$stmt->bindColumn('numeric_column', $intValue, \PDO::PARAM_INT);\n\n// Fetch the data\n$stmt->fetch(\PDO::FETCH_BOUND);