<?php\nheader('Content-Type: application/xspf+xml');\n\n$trackId = isset($_GET['id']) ? $_GET['id'] : die('No track id provided');\n$dbConnection = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n\n$stmt = $dbConnection->prepare("SELECT title, artist, filepath FROM music WHERE id = :id LIMIT 1");\n$stmt->execute(['id' => $trackId]);\n$track = $stmt->fetch(PDO::FETCH_ASSOC);\n\nif (!$track) {\n    die('Track not found');\n}\n\n?>\n<?xml version="1.0" encoding="UTF-8"?>\n<playlist xmlns="http://xspf.org/ns/0/" version="1">\n  <title>Playlist</title>\n  <trackList>\n    <track>\n      <location><?php echo htmlspecialchars($track['filepath']); ?></location>\n      <title><?php echo htmlspecialchars($track['title']); ?></title>\n      <creator><?php echo htmlspecialchars($track['artist']); ?></creator>\n    </track>\n  </trackList>\n</playlist>