<?php\n$server_ip = '127.0.0.1'; // IP of the Ventrilo server\n$port = 3784;             // Default Ventrilo Telnet port\n\n$socket = fsockopen($server_ip, $port, $errno, $errstr);\nif (!$socket) {\n    die("Could not connect to " . $server_ip . ":" . $port . ". Error: " . $errstr);\n}\n\nfputs($socket, "status\n");\n$output = '';\n\nwhile (!feof($socket)) {\n    $output .= fgets($socket, 128);\n}\n\nfclose($socket);\n\n// Process output\npreg_match_all('/(\d+)\s+(.+?)\s+::\s(.+)/', substr($output, strpos($output, "Chan ID")), $matches, PREG_SET_ORDER);\n\necho "<ul>";\nforeach ($matches as $match) {\n    echo "<li>Channel: #" . $match[1] . " - Name: " . htmlspecialchars($match[2]) . "</li>";\n}\necho "</ul>";\n?>