try {\n    // Ensure Zend_Session::start() is called first\n    if (!Zend_Session::isStarted()) {\n        Zend_Session::start();\n    }\n\n    $usersession = new Zend_Session_Namespace('userdata');\n    \n    // Check if $user and $menuMap are properly set before storing them\n    if (isset($user) && !empty($user)) {\n        $usersession->user = $user;\n    } else {\n        throw new Exception('User data is not set.');\n    }\n\n    // Process and store menu map\n    $menuMap = $this->processMenuMap($menuMapPath);\n    if (is_array($menuMap) && !empty($menuMap)) {\n        $usersession->menus = $menuMap;\n    } else {\n        throw new Exception('Menu Map data is not set or invalid.');\n    }\n} catch (Zend_Session_Exception $zsEx) {\n    // Log session-related exceptions separately\n    error_log("Session exception: " . $zsEx->getMessage());\n} catch (Exception $ex) {\n    // Handle other exceptions\n    error_log("General exception: " . $ex->getMessage());\n}