<?php\n// Start the session with custom settings\n$sessionTimeOutInSeconds = 300; // 5 minutes\n\nini_set('session.gc_maxlifetime', $sessionTimeOutInSeconds);\nini_set('session.cookie_lifetime', 0); // Cookie ends when browser closes\n\n// Start the session\nsession_start();\n\n// Check for inactivity and destroy the session if necessary\nif (isset($_SESSION['LAST_ACTIVITY']) && (time() - $_SESSION['LAST_ACTIVITY'] > $sessionTimeOutInSeconds)) {\n    session_unset();     // Unset $_SESSION variable for the run-time \n    session_destroy();   // Destroy session data in storage\n}\n$_SESSION['LAST_ACTIVITY'] = time(); // Update last activity time stamp\n\n\n// Your login logic here\nif($errors == 0) {\n    $_SESSION['logged'] = "1";\n}\n\n// Check if user is logged in\nif(isset($_SESSION['logged']) && $_SESSION['logged'] == "1") {\n    echo "User is logged in.";\n} else {\n    echo "Access denied.";\n}\n?>