function findUniqueCharacters($possiblesString) {\n    $charCount = [];\n\n    // Count each character occurrence\n    for ($i = 0; $i < strlen($possiblesString); $i++) {\n        $char = $possiblesString[$i];\n        if (!isset($charCount[$char])) {\n            $charCount[$char] = 1;\n        } else {\n            $charCount[$char]++;\n        }\n    }\n\n    // Find unique characters\n    $uniqueChars = [];\n    foreach ($charCount as $char => $count) {\n        if ($count === 1) {\n            $uniqueChars[] = $char;\n        }\n    }\n\n    return $uniqueChars;\n}\n\n// Example usage:\n$possiblesString = "123456789123456";\n$uniqueCharacters = findUniqueCharacters($possiblesString);\nprint_r($uniqueCharacters); // Output will be the unique characters in the string