<?php\n// Load the webpage content\n$htmlContent = file_get_contents('your_webpage.html');\n\n// Create a new DOMDocument object\n$dom = new DOMDocument;\n\n// Suppress warnings and load the HTML content into the DOM document\n@$dom->loadHTML($htmlContent);\n\n// Create an XPath query to find all <span> elements with class "img"\n$xpath = new DOMXPath($dom);\n$spansWithClassImg = $xpath->query('//span[@class="img"]');\n\n// Loop through each span and extract the URL\n$urlArray = [];\nforeach ($spansWithClassImg as $span) {\n    // Assuming the URL is in an attribute like 'data-url'\n    if ($url = $span->getAttribute('data-url')) {\n        $urlArray[] = $url;\n    }\n}\n\n// Output the array of URLs\nprint_r($urlArray);\n?>