// Sample function to check if a user has access to premium content based on their role.\nfunction has_premium_access($user_id) {\n    $user = get_userdata($user_id);\n    if (in_array('premium_member', (array) $user->roles)) {\n        return true;\n    }\n    return false;\n}\n\n// Usage of the function\n$user_has_access = has_premium_access(get_current_user_id());\nif ($user_has_access) {\n    echo 'Welcome to premium content!';\n} else {\n    echo 'Please upgrade your membership for full access.';\n}