<?php\nsession_start();\n\n// Check if the 'refreshed' flag is not set, it means new load (not refreshed)\nif (!isset($_SESSION['refreshed'])) {\n    $_SESSION['refreshed'] = false;\n} else {\n    // If session variable exists and is true, it's a refresh\n    $_SESSION['refreshed'] = false;\n    echo "The page was refreshed!";\n}\n\n// Set the flag to true indicating that this load might be because of a refresh on next request\n$_SESSION['refreshed'] = true;\n\n?>