<?php\n$allowed_referrer = 'http://sy-stu.org'; // Your website URL\n$file_path = '/path/to/your/file.zip'; // Path to your file\n\nif (isset($_SERVER['HTTP_REFERER']) && strpos($_SERVER['HTTP_REFERER'], $allowed_referrer) !== false) {\n    // User is coming from an allowed referrer, serve the file\n    if (file_exists($file_path)) {\n        header('Content-Description: File Transfer');\n        header('Content-Type: application/octet-stream');\n        header('Content-Disposition: attachment; filename="'.basename($file_path).'"');\n        header('Expires: 0');\n        header('Cache-Control: must-revalidate');\n        header('Pragma: public');\n        header('Content-Length: ' . filesize($file_path));\n        readfile($file_path);\n        exit;\n    } else {\n        echo "File not found.";\n    }\n} else {\n    // Redirect to your website or show a download page\n    header("Location: http://sy-stu.org/download.php");\n    exit;\n}\n?>