use PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\nrequire 'path/to/your/vendor/autoload.php'; // Adjust the path to the autoload file\n\n$mail = new PHPMailer(true);\n\ntry {\n    $mail->isSMTP();\n    $mail->Host       = 'mail.domain.com';\n    $mail->SMTPAuth   = true;\n    $mail->Port       = 587; // or 465, check Exchange server SMTP settings\n    $mail->Username   = 'user@domain.com'; // Full email address\n    $mail->Password   = 'pass';\n    $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS; // Adjust if using SSL\n\n    // Recipients\n    $mail->setFrom('no-reply@domain.com', 'No-Reply');\n    $mail->addAddress($emailto);\n\n    // Content\n    $mail->isHTML(true);\n    $mail->Subject = $subject;\n    $mail->Body    = '<html><body>' . $today . $announcements . $food . $upcoming . '</body></html>';\n\n    $mail->send();\n    echo 'Message has been sent';\n} catch (Exception $e) {\n    echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";\n}