require 'vendor/autoload.php';\n\nuse PhpImap\Mailbox;\n\n// Path to your attachment dir\n$attachmentsDir = '/your/download/path/';\n\n$mailbox = new Mailbox('imap.gmail.com:993/imap/ssl', '<email>', '<password>');\n\n$emailsIDs = $mailbox->searchMailbox('ALL');\n\nif(!$emailsIDs) {\n    die('Emails not found in your inbox');\n}\n\nforeach ($emailsIDs as $emailID) {\n\n    // Get email object with header information\n    $mail = $mailbox->getMail($emailID);\n\n    if(isset($mail->attachments)) { // Check attachments\n\n        foreach($mail->attachments as $attachment) {\n\n            $filePath = $attachmentsDir . '/' . $attachment->filename;\n            file_put_contents($filePath, $attachment->getContent());\n        }\n    }\n\n}