<?php\n$filesource = $_SERVER['HTTP_REFERER'];\n$a = fopen($filesource, "r"); //fopen("html_file.html","r");\n$string = fread($a, 1024);\nfclose($a); // Always close the file after reading\n\nif (preg_match("/<linkto>(.*?)<\/linkto>/i", $string, $out)) {\n    $outdata = $out[1];\n} else if (preg_match("/<title>(.*?)<\/title>/i", $string, $titleMatch)) {\n    $outdata = $titleMatch[1];\n} else {\n    $outdata = "No linkto or title found";\n}\n\n// Assuming you want to print the first word of the output data\n$outdatapart = explode(" ", $outdata);\necho htmlspecialchars($outdatapart[0]); // Use htmlspecialchars to prevent XSS\n?>