<?php\n// Start the session\nsession_start();\n\n// Assume you have fetched data and want to navigate to another page with an ID\n$id = 1; // This would come from your database loop or input\n\n// Store the id in a session variable\n$_SESSION['mail_id'] = $id;\n\n// Optionally, store other variables in sessions as well\n$_SESSION['var1'] = 'some_value';\n$_SESSION['var2'] = 'another_value';\n\n// Redirect to another page where you want to use these values\nheader('Location: message.php');\nexit();\n?>