<?php\n// Start the session\nsession_start();\n\n// Check if session variable is set\nif (isset($_SESSION['mail_id'])) {\n    $id = $_SESSION['mail_id'];\n    \n    // Optionally, retrieve other variables\n    $var1 = isset($_SESSION['var1']) ? $_SESSION['var1'] : null;\n    $var2 = isset($_SESSION['var2']) ? $_SESSION['var2'] : null;\n\n    // Clear session variables if they are no longer needed (optional)\n    unset($_SESSION['mail_id'], $_SESSION['var1'], $_SESSION['var2']);\n\n    // Use $id, $var1, and $var2 in your application\n    echo "Mail ID: " . htmlspecialchars($id) . "<br>";\n} else {\n    echo "No mail ID found.";\n}\n?>