<?php\n$file = 'global.css';\n$lastModifiedTime = filemtime($file);\n\n// Set headers\nheader('Content-type: text/css');\nheader('Cache-Control: max-age=2592000, public'); // 31 days caching\nheader('Expires: ' . gmdate("D, d M Y H:i:s", $lastModifiedTime + 2592000) . ' GMT');\nheader('Last-Modified: '.gmdate('D, d M Y H:i:s', $lastModifiedTime).' GMT');\n\nif (isset($_SERVER['HTTP_IF_MODIFIED_SINCE']) && strtotime($_SERVER['HTTP_IF_MODIFIED_SINCE']) == $lastModifiedTime) {\n    header('HTTP/1.1 304 Not Modified');\n    exit;\n}\n\nif(extension_loaded('zlib')) {\n    ob_start('ob_gzhandler');\n}\ninclude($file);\n?>