<?php\n\nrequire 'vendor/autoload.php';\n\nuse GuzzleHttp\Psr7\Uri;\nuse GuzzleHttp\Psr7\Utils;\n\n$uri = new Uri('https://example.com/path?query=123#fragment');\n\n// Get components of the URI\necho $uri->getScheme(); // Outputs: https\necho "\n";\necho $uri->getPath();   // Outputs: /path\necho "\n";\necho $uri->getQuery();  // Outputs: query=123\necho "\n";\n\n// Modify parts of the URI\n$newUri = $uri->withPath('/new-path')->withQuery('query=changed');\n\necho (string)$newUri;    // Outputs: https://example.com/new-path?query=changed#fragment\n\n// Resolve a relative URL\n$baseUri = new Uri('http://example.org/base/path/');\n$resolvedUri = Utils::resolveReference($baseUri, '../relative/uri');\necho (string)$resolvedUri; // Outputs: http://example.org/relative/uri