class Users extends Zend_Db_Table_Abstract {\n    protected $_name = 'users';\n    protected $_primary = 'user_id';\n    protected $_rowClass = 'User';\n\n    public function getUserById($id) {\n        $select = $this->select()\n                       ->where('user_id = ?', (int)$id);\n        return $this->fetchRow($select);\n    }\n}\n\nclass User extends Zend_Db_Table_Row_Abstract {\n    // Example method that might interact with related Widgets\n    public function getWidgets() {\n        $widgetsTable = new Widgets();\n        $select = $widgetsTable->select()\n                              ->where('user_id = ?', (int)$this->user_id);\n        return $widgetsTable->fetchAll($select);\n    }\n}\n\nclass Widgets extends Zend_Db_Table_Abstract {\n    protected $_name = 'widgets';\n    protected $_rowClass = 'Widget';\n\n    public function getWidgetsFromUser($userId) {\n        $select = $this->select()\n                       ->where('user_id = ?', (int)$userId);\n        return $this->fetchAll($select);\n    }\n}\n\nclass Widget extends Zend_Db_Table_Row_Abstract {\n    // Methods related to a single widget\n}