<?php\n\n// Helper class for form handling\nclass FormHelper {\n    public function isPost() {\n        return $_SERVER['REQUEST_METHOD'] === 'POST';\n    }\n\n    public function getPost($key, $default = '') {\n        return isset($_POST[$key]) ? $_POST[$key] : $default;\n    }\n\n    public function isEmail($email) {\n        return filter_var($email, FILTER_VALIDATE_EMAIL);\n    }\n\n    public function isEmpty($value) {\n        return empty(trim($value));\n    }\n}\n\n// Validation class\nclass FormValidator {\n    protected $errors = [];\n\n    public function addError($field, $message) {\n        $this->errors[$field] = $message;\n    }\n\n    public function getErrors() {\n        return $this->errors;\n    }\n\n    public function isValid() {\n        return empty($this->errors);\n    }\n}\n\n// Controller\nclass ContactController {\n    protected $formHelper;\n\n    public function __construct(FormHelper $formHelper) {\n        $this->formHelper = $formHelper;\n    }\n\n    public function contact() {\n        $validator = new FormValidator();\n\n        if ($this->formHelper->isPost()) {\n            $name = $this->formHelper->getPost('name');\n            $email = $this->formHelper->getPost('email');\n            $contact = $this->formHelper->getPost('contact');\n            $message = $this->formHelper->getPost('message');\n\n            if ($this->formHelper->isEmpty($name)) {\n                $validator->addError('name', 'Please fill in your name.');\n            }\n\n            if ($this->formHelper->isEmpty($email) || !$this->formHelper->isEmail($email)) {\n                $validator->addError('email', 'Please fill in a valid email address.');\n            }\n\n            // Add other validations as needed\n\n            if ($validator->isValid()) {\n                // Process the form data (e.g., send an email)\n                header("Location: thank_you.php");\n                exit;\n            }\n        }\n\n        $this->view('contact_form', [\n            'errors' => $validator->getErrors(),\n            'postVars' => $_POST\n        ]);\n    }\n\n    private function view($template, $data = []) {\n        // Render the view with data\n        include $template . '.php';\n    }\n}\n\n// Usage\n$formHelper = new FormHelper();\n$contactController = new ContactController($formHelper);\n$contactController->contact();\n?>