<?php\n   // Secure PHP script to restart Tomcat service\n   if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['restart_tomcat'])) {\n       // Ensure only specific users can perform this action if needed\n       // $allowed_users = ['qcuser1', 'qcuser2'];\n       // if (in_array($_SESSION['username'], $allowed_users)) {\n\n       // Execute the command using system()\n       exec('sudo /usr/sbin/service tomcat8 restart 2>&1', $output, $return_var);\n\n       if ($return_var === 0) {\n           echo "Tomcat restarted successfully.";\n       } else {\n           echo "Error restarting Tomcat: ";\n           foreach ($output as $line) {\n               echo htmlspecialchars($line) . "<br>";\n           }\n       }\n\n       // }\n   ?>\n   <html>\n   <body>\n       <form method="post" action="">\n           <button type="submit" name="restart_tomcat">Restart Tomcat</button>\n       </form>\n   </body>