class Controller {\n    protected $models = [];\n\n    function __construct() {\n        parent::__construct();\n        $this->loadModel("AccountModel", "Model");\n        $account = $this->getUserAccount();\n        // ...\n    }\n\n    function loadModel($model_name, $var_name) {\n        if (!isset($this->models[$var_name])) {\n            require_once 'path/to/models/' . $model_name . '.php'; // Adjust path as necessary\n            $class_name = $model_name;\n            $obj = new $class_name();\n            $this->$var_name = $obj;\n            $this->models[$var_name] = true; // Track that this model is loaded for this instance\n        }\n    }\n\n    function getUserAccount() {\n        return $this->Model->getAccount($_SESSION["account"]["user_account_id"]);\n    }\n}