<?php\n// Connect to the database (replace with your actual DB connection details)\n$conn = mysqli_connect("localhost", "username", "password", "database");\n\nif (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// Assume $nid is defined somewhere in your code or passed as a parameter\n$nid = 1; // Example, replace with actual logic to set $nid\n\n// Use prepared statement to prevent SQL injection\n$stmt = $conn->prepare("SELECT tblNavPC.cID, tblNav.NavName \n                         FROM tblNavPC \n                         INNER JOIN tblNav ON tblNavPC.cID = tblNav.NavID \n                         WHERE tblNavPC.PID = ?");\n\nif ($stmt === false) {\n    die('Prepare error: ' . $conn->error);\n}\n\n// Bind the parameter\n$stmt->bind_param("i", $nid);\n\n// Execute the query\n$stmt->execute();\n\n// Get result\n$result = $stmt->get_result();\n\necho '<tr>\n          <td align="left" valign="top" class="medium"><h1>Your Nav Title Here</h1></td>\n      </tr>';\n\nwhile ($row = $result->fetch_assoc()) {\n    echo '<tr>\n              <td align="left" valign="top" class="medium">\n                  <a href="cat_ap~pnid~' . $nid . '~nid~' . $row['cID'] . '~post.htm">' . htmlspecialchars($row['NavName']) . '</a>\n              </td>\n          </tr>';\n}\n\n// Close the statement and connection\n$stmt->close();\n$conn->close();\n?>