class Application_Model_Tag extends Zend_Db_Table_Abstract {\n    protected $_dependentTables = array('Application_Model_TagObject');\n    protected $_name            = 'tag';\n\n    // Existing methods...\n    \n    public function getTagsWithOccurrences() {\n        $select = $this->select()->setIntegrityCheck(false)\n                     ->from(array('t' => 'tag'), array('id', 'name'))\n                     ->join(array('to' => 'tag_object'), 't.id = to.tag_id')\n                     ->group('t.id')\n                     ->order('COUNT(to.object_id) DESC');\n        $result = $this->fetchAll($select);\n        \n        return $result;\n    }\n}