function fetchCalendarsConcurrently($urls) {\n    $channels = [];\n    $data = [];\n\n    // Initialize multi-curl handler\n    $mh = curl_multi_init();\n\n    foreach ($urls as $url => $index) {\n        $ch = curl_init();\n        curl_setopt($ch, CURLOPT_URL, $url);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n        curl_multi_add_handle($mh, $ch);\n        $channels[$index] = $ch;\n    }\n\n    $active = null;\n\n    do {\n        $mrc = curl_multi_exec($mh, $active);\n    } while ($mrc == CURLM_CALL_MULTI_PERFORM);\n\n    while ($active && $mrc == CURLM_OK) {\n        if (curl_multi_select($mh) != -1) {\n            do {\n                $mrc = curl_multi_exec($mh, $active);\n            } while ($mrc == CURLM_CALL_MULTI_PERFORM);\n        }\n    }\n\n    foreach ($channels as $index => $ch) {\n        $data[$index] = json_decode(curl_multi_getcontent($ch), true);\n        curl_multi_remove_handle($mh, $ch);\n    }\n\n    curl_multi_close($mh);\n\n    return $data;\n}\n\n// Usage\n$calendarUrls = [\n    0 => 'https://www.googleapis.com/calendar/v3/calendars/your.cal.id/events?key=YOUR_API_KEY',\n    1 => 'https://www.googleapis.com/calendar/v3/calendars/another.cal.id/events?key=YOUR_API_KEY',\n    // Add more URLs as needed\n];\n\n$calendarData = fetchCalendarsConcurrently($calendarUrls);