<?php\n$countries = [\n    'US' => ['Alabama', 'Alaska', /* ... other states ... */, 'Wyoming'],\n    'CA' => ['Alberta', 'British Columbia', /* ... other provinces ... */, 'Yukon']\n];\n\n$countryId = $_POST['country_id'];\n\nif (array_key_exists($countryId, $countries)) {\n    echo '<option value="">Select State/Province</option>';\n    foreach ($countries[$countryId] as $state) {\n        echo "<option value='$state'>$state</option>";\n    }\n} else {\n    echo '<option value="">None Applicable</option>';\n}\n?>