<?php\n// Check PHP Version\necho "PHP Version: " . phpversion() . "\n";\n\n// Check current working directory\n$currentDir = getcwd();\necho "Current Directory: $currentDir\n";\n\n// Example of using DIRECTORY_SEPARATOR\n$filePath = __DIR__ . DIRECTORY_SEPARATOR . 'example.txt';\necho "Example File Path: $filePath\n";\n\n// Output environment variables related to PHP\nforeach ($_ENV as $key => $value) {\n    echo "Environment Variable [$key]: $value\n";\n}\n?>