// src/Controller/ErrorController.php\nnamespace App\Controller;\n\nuse Throwable;\nuse Symfony\Component\HttpFoundation\Response;\nuse Symfony\Component\HttpKernel\Log\DebugLoggerInterface;\nuse Twig\Environment;\n\nclass ErrorController extends \Symfony\Bundle\FrameworkBundle\Controller\AbstractController\n{\n    public function show(\n        Throwable $exception,\n        ?DebugLoggerInterface $logger = null,\n        bool $debug,\n        Environment $twig\n    ): Response {\n        // Handle the exception however you want\n        return new Response($twig->render('bundles/TwigBundle/Exception/error.html.twig', [\n            'status_code' => $exception->getCode(),\n            'status_text' => isset(Response::$statusTexts[$exception->getStatusCode()]) ? Response::$statusTexts[$exception->getStatusCode()] : '',\n            'exception' => $exception,\n            'logger' => $logger,\n            'currentContent' => null,\n        ]), 200);\n    }\n}