<?php\n\nfunction getFlvDimensions($filePath) {\n    // Check if ffprobe is installed\n    if (!shell_exec('command -v ffprobe')) {\n        return false;\n    }\n\n    // Command to run ffprobe\n    $cmd = "ffprobe -v error -select_streams v:0 -show_entries stream=width,height -of csv=p=0:s=x " . escapeshellarg($filePath);\n\n    // Execute command and get output\n    exec($cmd, $output);\n\n    if (isset($output[0]) && strpos($output[0], 'x') !== false) {\n        list($width, $height) = explode('x', $output[0]);\n        return ['width' => $width, 'height' => $height];\n    }\n\n    return false;\n}\n\n// Example usage\n$filePath = '/path/to/your/video.flv';\n$dimensions = getFlvDimensions($filePath);\n\nif ($dimensions !== false) {\n    echo "Width: {$dimensions['width']}, Height: {$dimensions['height']}";\n} else {\n    echo "Could not determine dimensions.";\n}\n?>