$directory = $mainframe->getCfg('absolute_path') . "/images/store/";\n$files = scandir($directory);\n\n// Remove '.' and '..' from the array\n$files = array_diff($files, array('.', '..', 'index.html', 'index.php', 'Thumbs.db'));\n\n$thelist = '';\n\nforeach ($files as $file) {\n    $strExt = pathinfo($file, PATHINFO_EXTENSION);\n    if ($strExt == 'jpg') {\n        $Link = 'index.php?option=com_shop&task=deleteFile&file[]=' . urlencode($file);\n        $thelist .= '<tr class="row0"><td nowrap="nowrap"><a href="' . htmlspecialchars($Link) . '">' . htmlspecialchars($file) . '</a></td>' . "\n";\n        $thelist .= '<td align="center" class="order"><a href="' . htmlspecialchars($Link) . '" title="delete"><img src="/administrator/images/publish_x.png" width="16" height="16" alt="delete"></a></td></tr>' . "\n";\n    }\n}\n\necho $thelist;