<?php\nheader("Content-Type: text/plain");\n\n// Send headers to prevent caching and keep alive the connection\nnocache_headers();\nheader('Cache-Control: no-cache, no-store, max-age=0, must-revalidate');\nheader('Pragma: no-cache');\n\nfunction nocache_headers() {\n    header('Expires: Thu, 19 Nov 1981 08:52:00 GMT'); // Date in the past\n}\n\nwhile(true) {\n    // Simulate some data being available after a few seconds\n    sleep(5);\n    \n    // Send data to client\n    echo "Server time is: " . date("H:i:s") . "\n";\n    flush();\n}\n?>