function processRequest($requestId) {\n    // Connect to the database\n    $pdo = new PDO('mysql:host=localhost;dbname=test', 'username', 'password');\n    \n    try {\n        // Start a transaction\n        $pdo->beginTransaction();\n        \n        // Simulate processing the request\n        sleep(2);\n        \n        // Update something in the database based on the request\n        $stmt = $pdo->prepare("UPDATE table_name SET processed = 1 WHERE id = :id");\n        $stmt->execute(['id' => $requestId]);\n        \n        // Commit the transaction\n        $pdo->commit();\n    } catch (Exception $e) {\n        // Rollback in case of error\n        $pdo->rollBack();\n        throw $e;\n    }\n}\n\n// Example usage would be: processRequest($_GET['request_id']);