function get_title_dom($url) {\n    $ch = curl_init();\n    curl_setopt_array($ch, array(\n        CURLOPT_URL            => $url,\n        CURLOPT_RETURNTRANSFER => 1\n    ));\n    $output = curl_exec($ch);\n    curl_close($ch);\n\n    if ($output === FALSE) {\n        return "Error fetching content using cURL";\n    }\n\n    $dom = new DOMDocument();\n    @$dom->loadHTML($output); // Using the '@' operator to suppress warnings from malformed HTML\n\n    $titleNode = $dom->getElementsByTagName('title')->item(0);\n    return $titleNode ? trim($titleNode->textContent) : 'No title found';\n}\n\n$url = "https://example.com";\n$title = get_title_dom($url);\necho $title;