function get_title($url) {\n    $tags = get_meta_tags($url);\n    if (isset($tags['title'])) {\n        return $tags['title'];\n    } else {\n        // Fallback method if meta tag is not present\n        $content = file_get_contents($url);\n        if ($content === FALSE) {\n            return "Error fetching content";\n        }\n        preg_match('/<title>(.*?)<\/title>/is', $content, $matches);\n        return isset($matches[1]) ? trim($matches[1]) : 'No title found';\n    }\n}\n\n$url = "https://example.com";\n$title = get_title($url);\necho $title;