$data = "This data needs to be encrypted.";\n   $key = openssl_random_pseudo_bytes(32); // AES-256 requires a 32-byte key.\n   $ivlen = openssl_cipher_iv_length($cipher="aes-256-cbc");\n   $iv = openssl_random_pseudo_bytes($ivlen);\n   \n   $ciphertext = openssl_encrypt($data, $cipher, $key, $options=0, $iv);\n\n   // To decrypt\n   $decryptedData = openssl_decrypt($ciphertext, $cipher, $key, $options=0, $iv);