use PublicSuffix\ListUpdater;\nuse PublicSuffix\Manager;\n\n// Update the public suffix list (once in a while)\n$updater = new ListUpdater();\n$updater->update();\n\n// Initialize the manager with the updated list\n$manager = new Manager();\n\n// Example URL\n$url = 'http://www.google.co.uk/foo';\n\n// Parse the host\n$parsedUrl = parse_url($url);\n$domainWithSubdomain = $parsedUrl['host'];\n\n// Extract the registerable domain (e.g., google.co.uk)\n$registerableDomain = $manager->getRegisterableDomain($domainWithSubdomain);\n\necho $registerableDomain; // Outputs: google.co.uk