<?php\n\nset_time_limit(0); // Run indefinitely\nignore_user_abort(true);\n\nwhile (true) {\n    // Connect to MySQL and check for new entries\n    $pdo = new PDO('mysql:host=localhost;dbname=your_db', 'user', 'pass');\n    $stmt = $pdo->query("SELECT * FROM your_table WHERE status='pending' LIMIT 1");\n    $row = $stmt->fetch(PDO::FETCH_ASSOC);\n\n    if ($row) {\n        // Run FFmpeg command\n        exec("ffmpeg -i input.mp4 -codec: copy output.mp4", $output, $return_var);\n        if ($return_var === 0) {\n            // Update database with output info\n            $pdo->exec("UPDATE your_table SET status='processed' WHERE id={$row['id']}");\n        } else {\n            // Handle error\n            echo "FFmpeg failed with output: " . implode("\n", $output);\n        }\n    }\n\n    sleep(5); // Sleep for 5 seconds before checking again\n}