<?php\n\n$x = $y = 100;\n\n$gd = imagecreatetruecolor($x, $y);\n\n$w = imagecolorallocate($gd, 255, 255, 255);\n$b = imagecolorallocate($gd, 0, 0, 0);\n\n\nfor ($r = 0; $r < $y; $r++) {\n    for ($c = 0; $c < $x; $c++) {\n        if (rand(0,1) == 0) {\n            $rand = $w;\n        } else {\n            $rand = $b;\n        }\n        imagesetpixel($gd, $c, $r, $rand);\n    }\n}\n\nheader('Content-Type: image/png');\nimagepng($gd);\nimagedestroy($gd);\n\n?>