class UserForm {\n    public function validateInput($data) {\n        // Validate form input at UI level\n        if (!filter_var($data['email'], FILTER_VALIDATE_EMAIL)) {\n            throw new Exception("Invalid email format");\n        }\n        return true;\n    }\n}\n\nclass UserModel {\n    public function saveUser($data) {\n        // Validate data according to business rules and model constraints\n        if (empty($data['username'])) {\n            throw new Exception("Username is required");\n        }\n        if (strlen($data['password']) < 8) {\n            throw new Exception("Password must be at least 8 characters long");\n        }\n        \n        // Proceed with saving user to the database\n        $this->insertIntoDatabase($data);\n    }\n\n    private function insertIntoDatabase($data) {\n        // Database interaction logic here\n    }\n}