class MyDatabase {\n    private $dbConn;\n\n    public function connect($dbHost, $dbName, $dbUser, $dbPass) {\n        $this->dbConn = new mysqli($dbHost, $dbUser, $dbPass, $dbName);\n        if ($this->dbConn->connect_error) {\n            die("Connection failed: " . $this->dbConn->connect_error);\n        }\n    }\n\n    public function insertDemo($id, $name, $score, $dept, $date) {\n        $stmt = $this->dbConn->prepare("INSERT INTO `demo` (`id`, `name`, `score`, `dept`, `date`) VALUES (?, ?, ?, ?, ?)");\n        if ($stmt === false) {\n            die("Prepare failed: " . $this->dbConn->error);\n        }\n        $stmt->bind_param("isiss", $id, $name, $score, $dept, $date);\n        $stmt->execute();\n        $stmt->close();\n    }\n\n    public function close() {\n        $this->dbConn->close();\n    }\n}\n\n// Usage\n$MyDB = new MyDatabase();\n$MyDB->connect('localhost', 'your_db_name', 'your_username', 'your_password');\n\n$id = 1;\n$name = "James Kablammo";\n$score = 1205550;\n$dept = "Marketing";\n$date = date("Y-m-d H:i:s"); // Ensure you have a valid date format\n\n$MyDB->insertDemo($id, $name, $score, $dept, $date);\n\n$MyDB->close();