<?php\n\nfunction getDistanceBetweenZipCodes($zipCode1, $zipCode2) {\n    // Database connection (adjust details as necessary)\n    $servername = "localhost";\n    $username = "your_username";\n    $password = "your_password";\n    $dbname = "your_database";\n\n    try {\n        $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\n        // set the PDO error mode to exception\n        $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // SQL query to get latitude and longitude for both zip codes\n        $stmt = $conn->prepare("SELECT LATITUDE, LONGITUDE FROM your_table WHERE ZIP IN (:zip1, :zip2)");\n        $stmt->bindParam(':zip1', $zipCode1);\n        $stmt->bindParam(':zip2', $zipCode2);\n        $stmt->execute();\n\n        // Check if both zip codes are valid\n        $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n        if (count($results) != 2) {\n            throw new Exception("One or more ZIP codes not found.");\n        }\n\n        // Extract latitude and longitude for each zip code\n        list($lat1, $lon1) = array($results[0]['LATITUDE'], $results[0]['LONGITUDE']);\n        list($lat2, $lon2) = array($results[1]['LATITUDE'], $results[1]['LONGITUDE']);\n\n    } catch(PDOException $e) {\n        echo "Error: " . $e->getMessage();\n        return null;\n    }\n\n    // Haversine formula to calculate distance\n    $earthRadius = 6371; // Kilometers\n    $dLat = deg2rad($lat2 - $lat1);\n    $dLon = deg2rad($lon2 - $lon1);\n\n    $a = sin($dLat/2) * sin($dLat/2) + cos(deg2rad($lat1)) * cos(deg2rad($lat2)) * sin($dLon/2) * sin($dLon/2);\n    $c = 2 * atan2(sqrt($a), sqrt(1-$a));\n\n    $distance = $earthRadius * $c;\n\n    return $distance;\n}\n\n// Example usage\n$zipCode1 = "10001"; // New York, NY\n$zipCode2 = "94080"; // Mountain View, CA\n\n$distance = getDistanceBetweenZipCodes($zipCode1, $zipCode2);\nif ($distance !== null) {\n    echo "The distance between ZIP code $zipCode1 and ZIP code $zipCode2 is $distance km.";\n}\n\n$conn = null; // Close the connection\n?>