function showAllComments($article_id, $param)\n{\n    $article = Article::getById($article_id);\n\n    if (!empty($article)) {\n        // Set the title for the page using the article's title\n        UI::setTitle('Kiubbo.com :: ' . htmlspecialchars($article->getTitle()));\n\n        ?>\n        <div class="news_item">\n            <h2 class="news_item_title"><b><a href="<?php echo htmlspecialchars($article->getUrl()); ?>"><?php echo htmlspecialchars($article->getTitle()); ?></a></b></h2>              \n        </div>\n        <?php\n    }\n}