// Define a namespace for a class\nnamespace MyProject\SubNamespace {\n\n    class MyClass {\n        public function sayHello() {\n            echo "Hello from MyClass";\n        }\n    }\n\n    // Function within the same namespace\n    function myFunction() {\n        echo "Hello from myFunction";\n    }\n\n}\n\n// Accessing the class and function from outside its namespace\n$object = new MyProject\SubNamespace\MyClass();\n$object->sayHello();\n\nMyProject\SubNamespace\myFunction();