// As a list (ordered collection)\n$list = [10, 20, 30];\n\n// As a set (unique values): Use array_unique or a workaround with associative arrays\n$set = ['apple', 'banana'];\n$set[] = 'orange';\n$set = array_values(array_unique($set)); // Ensuring all elements are unique\n\n// Associative array as a map/dictionary\n$map = [\n    'key1' => 'value1',\n    'key2' => 'value2'\n];\n\n// Stack (LIFO): Using array_push, array_pop\n$stack = [];\narray_push($stack, 1, 2, 3);\n$val = array_pop($stack); // Returns 3\n\n// Queue (FIFO): Using array_unshift and array_shift\n$queue = [];\narray_push($queue, 'task1', 'task2');\n$currentTask = array_shift($queue); // Returns 'task1'