<?php\n$servername = filter_input(INPUT_GET, 'server', FILTER_SANITIZE_STRING);\n$username = filter_input(INPUT_GET, 'username', FILTER_SANITIZE_STRING);\n$password = filter_input(INPUT_GET, 'password', FILTER_SANITIZE_STRING);\n$dbname = filter_input(INPUT_GET, 'database', FILTER_SANITIZE_STRING);\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$content = filter_input(INPUT_GET, 'content', FILTER_SANITIZE_STRING);\n\nif ($content == "tables") {\n    $result = $conn->query("SHOW TABLES");\n    $xml = "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n";\n    $xml .= "<tables>";\n    while ($row = $result->fetch_assoc()) {\n        foreach($row as $table) {\n            $xml .= "<table>" . htmlspecialchars($table) . "</table>";\n        }\n    }\n    $xml .= "</tables>";\n    header('Content-type: text/xml');\n    echo $xml;\n}\n$conn->close();\n?>