$w_time_no = '0000-00-00 22:00:00';\n\necho '<br />1. ' . $w_time_no;\n\n// Extract the time part if the date is invalid\nif (strpos($w_time_no, '0000-00-00') !== false) {\n    // Use only the time part\n    $timePart = substr($w_time_no, 11);\n    echo '<br />2. Invalid Date but Time Extracted';\n    echo '<br />3. ' . date('G:i', strtotime('today ' . $timePart));\n} else {\n    echo '<br />2. ' . strtotime($w_time_no);\n    echo '<br />3. ' . date('G:i', strtotime($w_time_no));\n}