<?php\n$sql = "{? = call YourStoredProcedureWithOutputParam(?)}";\n$params = array(\n    array(0, SQLSRV_PARAM_INOUT), // Return value holder\n    $yourInputParameter         // Input parameter for your stored procedure\n);\n\n$stmt = sqlsrv_query($conn, $sql, $params);\nif ($stmt === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n// Check if the statement was successful\nif (sqlsrv_next_result($stmt)) {\n    echo "Return Code: " . $params[0];\n} else {\n    echo "Error executing stored procedure.";\n}\n?>