<?php\n\nrequire 'vendor/autoload.php';\n\n$loop = React\EventLoop\Factory::create();\n$socket = new React\Socket\Server('0.0.0.0:5001', $loop);\n\n$socket->on('connection', function (React\Stream\ConnectionInterface $conn) {\n    $conn->write("Hello!\n");\n\n    $conn->on('data', function ($data) use ($conn) {\n        echo $data;\n        $conn->write($data);\n    });\n});\n\necho "Socket server listening on port 5001\n";\n\n$loop->run();