class TestClass {\n    private $data = [];\n\n    public function __construct() {\n        $this->data['firstValue'] = "cheese";\n    }\n\n    public function getValue($key) {\n        return array_key_exists($key, $this->data) ? $this->data[$key] : null;\n    }\n}\n\n$test = new TestClass();\necho $test->getValue('firstValue');  // Outputs: cheese\necho $test->getValue('nonExistentKey');  // Outputs nothing (or null)